 document.addEventListener('DOMContentLoaded', () => {
    const regionItems = document.querySelectorAll('.region-list ul li');
    const countryContainers = document.querySelectorAll('.country-container');
    const detailedLists = document.querySelectorAll('.detailed-list');
    const toggleButton = document.getElementById('toggle-button');

    if (toggleButton) {
        toggleButton.addEventListener('click', () => {
            if (window.matchMedia('(max-width: 768px)').matches) {
                document.querySelector('.region-list').style.display = 'block';
                document.querySelector('.content-display').style.display = 'none';
            }
        });
    }

    function showCountryList(regionId) {
        countryContainers.forEach(container => {
            container.classList.remove('show');
        });
        detailedLists.forEach(list => {
            list.classList.remove('show');
        });
        const regionElement = document.getElementById(regionId);
        if (regionElement) {
            regionElement.classList.add('show');
        }
        if (window.matchMedia('(max-width: 768px)').matches) {
            document.querySelector('.region-list').style.display = 'none';
            document.querySelector('.content-display').style.display = 'block';
        }
    }

    if (regionItems) {
        regionItems.forEach(item => {
            item.addEventListener('click', (event) => {
                const regionId = event.target.dataset.region;
                showCountryList(regionId);
            });
        });

        // Automatically simulate click on a specific region after the page loads (e.g., 'africa')
        const defaultRegion = regionItems[0]; // Change the index to select a different default region
        if (defaultRegion) {
            defaultRegion.click(); // Simulates the click event on page load
        }
    }

    const countryLinks = document.querySelectorAll('.country-container ul li a');
    if (countryLinks) {
        countryLinks.forEach(country => {
            country.addEventListener('click', function(event) {
                event.preventDefault();
                const countryId = this.getAttribute('data-country') + '-details';
                const detailedList = document.getElementById(countryId);
                detailedLists.forEach(list => {
                    list.classList.remove('show');
                });
                if (detailedList) {
                    detailedList.classList.add('show');
                }
            });
        });
    }
});
