// Initialize Leaflet map
const map = L.map('map').setView([51.505, -0.09], 2);
L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
    attribution: '© OpenStreetMap',
    subdomains: ['a', 'b', 'c']
}).addTo(map);

// Create a layer group to hold the country polygons
const countryPolygons = L.layerGroup().addTo(map);

// Create a layer group to hold the court markers
const courtMarkers = L.layerGroup().addTo(map);

// Add event listeners to country links
document.querySelectorAll('.country-container ul li a').forEach((countryLink) => {
    countryLink.addEventListener('click', (e) => {
        e.preventDefault(); // Prevent default anchor behavior
        const countryName = e.target.dataset.country;
        const countryBounds = countryBoundsData[countryName];
        const courtData = countryCourtCoordinates[countryName];
        const courtLocation = courtData ? courtData.coords : null;
        const courtName = courtData ? courtData.name : '';

        // Log country name and coordinates
        console.log(`Country: ${countryName}, Coordinates: ${JSON.stringify(countryBounds)}`);
        console.log(`Court Location: ${courtName}, Coordinates: ${JSON.stringify(courtLocation)}`);

        // Display the map
        document.getElementById('map').style.display = 'block';

        // Clear the previous country polygon and court markers
        countryPolygons.clearLayers();
        courtMarkers.clearLayers();

        // Create a new polygon for the selected country
        const polygon = L.polygon(countryBounds, {
            color: 'red',
            weight: 2,
            opacity: 0.5
        }).addTo(countryPolygons);

        // Add court marker if available
        if (courtLocation) {
            L.marker(courtLocation)
                .addTo(courtMarkers)
                .bindPopup(`${courtName}`) // Popup displays court name
                .openPopup();
        }

        // Center the map on the country
        map.fitBounds(polygon.getBounds());
    });
});



document.querySelectorAll('.region-list ul li').forEach((region) => {
  region.addEventListener('click', () => {
    // Hide the map when a region is clicked
    document.getElementById('map').style.display = 'none';

  });
});
